package com.syntax.class01;

public class PracticeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//primitive data
		//storing whole numbers
		
		byte variableOne = -128;
		short variableTwo = 100;
		int variableThree=1000;
		long variableFour=20000L;
			//float and double
		float pi= 4.99f;
		double y= 11.99;
		
		//primitive data
		int age = 27;
		char Firstletterofhometown = 'A';
		char FirstWordofjava ='J';
		//boolean
		boolean study=true;
		boolean hungry=false;
		
		//Print out 
		System.out.println(variableOne);
		System.out.println(variableTwo);
		System.out.println(variableThree);
		System.out.println(variableFour);
		System.out.println(pi);
		System.out.println(y);
		System.out.println(Firstletterofhometown);
		System.out.println(FirstWordofjava);
		System.out.println(study);
		System.out.println(hungry);

	}

}
